client/src/components/timesheet-form.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Save, X, Calendar } from "lucide-react";
import { format, startOfWeek, addDays } from "date-fns";
import { z } from "zod";
import { insertWorkEntrySchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { WorkHourRequestWithUser, WorkEntry } from "@shared/schema";

import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Calendar as CalendarComponent } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";

const workEntryFormSchema = insertWorkEntrySchema.extend({
  date: z.string().min(1, "Date is required"),
  workType: z.string().min(1, "Work type is required"),
  description: z.string().min(1, "Description is required"),
  timeSpent: z.string().min(1, "Time spent is required"),
});

type WorkEntryFormData = z.infer<typeof workEntryFormSchema>;

export default function WorkEntryForm() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const todayDate = new Date().toISOString().split('T')[0];

  // Get approved work hour requests
  const { data: approvedRequests = [] } = useQuery<WorkHourRequestWithUser[]>({
    queryKey: ["/api/work-hour-requests/my"],
    queryFn: async () => {
      const response = await fetch("/api/work-hour-requests/my");
      if (!response.ok) {
        throw new Error("Failed to fetch work hour requests");
      }
      return response.json();
    },
  });

  // Get my existing work entries to check for duplicates
  const { data: myWorkEntries = [] } = useQuery<WorkEntry[]>({
    queryKey: ["/api/work-entries/my"],
    queryFn: async () => {
      const response = await fetch("/api/work-entries/my");
      if (!response.ok) {
        throw new Error("Failed to fetch work entries");
      }
      return response.json();
    },
  });

  // Get approved dates that haven't been filled yet
  const approvedDates = approvedRequests
    .filter(request => request.status === "approved")
    .map(request => request.requestedDate);

  // Get dates that already have work entries
  const existingEntryDates = myWorkEntries.map(entry => entry.date);

  // Filter out approved dates that already have entries
  const availableApprovedDates = approvedDates.filter(date => !existingEntryDates.includes(date));

  const form = useForm<WorkEntryFormData>({
    resolver: zodResolver(workEntryFormSchema),
    defaultValues: {
      date: todayDate,
      workType: "",
      description: "",
      timeSpent: "",
    },
  });

  const createWorkEntryMutation = useMutation({
    mutationFn: async (data: WorkEntryFormData) => {
      const response = await apiRequest("POST", "/api/work-entries", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/work-entries/my"] });
      queryClient.invalidateQueries({ queryKey: ["/api/stats"] });
      toast({
        title: "Success",
        description: "Work entry saved successfully!",
      });
      form.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save work entry",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: WorkEntryFormData) => {
    // Validate that time spent is a valid number
    const timeSpentNum = parseFloat(data.timeSpent);
    if (isNaN(timeSpentNum) || timeSpentNum <= 0) {
      toast({
        title: "Validation Error",
        description: "Time spent must be a valid number greater than 0",
        variant: "destructive",
      });
      return;
    }

    // Allow today's date and available approved request dates
    const today = new Date().toISOString().split('T')[0];
    const isToday = data.date === today;
    const isAvailableApprovedDate = availableApprovedDates.includes(data.date);

    if (!isToday && !isAvailableApprovedDate) {
      // Check if it's an approved date that already has an entry
      if (approvedDates.includes(data.date) && existingEntryDates.includes(data.date)) {
        toast({
          title: "Validation Error",
          description: "You have already added a work entry for this approved date",
          variant: "destructive",
        });
      } else {
        toast({
          title: "Validation Error",
          description: "You can only add entries for today's date or approved work hour request dates",
          variant: "destructive",
        });
      }
      return;
    }

    createWorkEntryMutation.mutate(data);
  };

  const handleClear = () => {
    form.reset({
      date: todayDate,
      workType: "",
      description: "",
      timeSpent: "",
    });
  };

  return (
    <Card data-testid="work-entry-form">
      <CardHeader>
        <CardTitle>Log Work Entry</CardTitle>
      </CardHeader>
      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <FormField
                control={form.control}
                name="date"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Date</FormLabel>
                    <Popover>
                      <PopoverTrigger asChild>
                        <FormControl>
                          <Button
                            variant="outline"
                            className="w-full justify-start text-left font-normal"
                            data-testid="button-date-picker-form"
                          >
                            <Calendar className="mr-2 h-4 w-4" />
                            {field.value ? format(new Date(field.value), "PPP") : "Pick a date"}
                          </Button>
                        </FormControl>
                      </PopoverTrigger>
                      <PopoverContent className="w-auto p-0" align="start">
                        <CalendarComponent
                          mode="single"
                          selected={field.value ? new Date(field.value) : undefined}
                          onSelect={(date) => {
                            if (date) {
                              field.onChange(format(date, 'yyyy-MM-dd'));
                            }
                          }}
                          disabled={(date) => {
                            // Get today's date at start of day for proper comparison
                            const today = new Date();
                            today.setHours(0, 0, 0, 0);
                            const compareDate = new Date(date);
                            compareDate.setHours(0, 0, 0, 0);

                            // Check if date is today
                            const isToday = compareDate.getTime() === today.getTime();

                            // Check if date is an available approved request date (not already filled)
                            const dateStr = format(date, 'yyyy-MM-dd');
                            const isAvailableApprovedDate = availableApprovedDates.includes(dateStr);

                            // Also check if it's a weekend (Saturday = 6, Sunday = 0)
                            const dayOfWeek = date.getDay();
                            const isWeekend = dayOfWeek === 0 || dayOfWeek === 6;

                            // Only disable weekends if the date is NOT approved (approved dates can be any weekday that was requested)
                            if (isAvailableApprovedDate) {
                              return false; // Always allow available approved dates regardless of day of week
                            }

                            // For non-approved dates, allow only today and disable weekends
                            return !isToday || isWeekend;
                          }}
                          initialFocus
                          data-testid="calendar-date-picker-form"
                        />
                      </PopoverContent>
                    </Popover>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="workType"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Work Type</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-work-type">
                          <SelectValue placeholder="Select work type" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="Task">Task</SelectItem>
                        <SelectItem value="Project">Project</SelectItem>
                        <SelectItem value="Meeting">Meeting</SelectItem>
                        <SelectItem value="Skill-up">Skill-up</SelectItem>
                        <SelectItem value="Partial Leave">Partial Leave</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="timeSpent"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Time Spent (hours)</FormLabel>
                    <FormControl>
                      <Input 
                        type="number" 
                        step="0.5"
                        min="0.5"
                        max="12"
                        placeholder="e.g. 2.5"
                        {...field}
                        data-testid="input-time-spent"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Work Description</FormLabel>
                  <FormControl>
                    <Textarea 
                      {...field}
                      value={field.value || ""}
                      rows={3}
                      placeholder="Brief description of work performed..."
                      data-testid="input-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex justify-end space-x-3">
              <Button 
                type="button" 
                variant="outline" 
                onClick={handleClear}
                data-testid="button-clear"
              >
                <X className="w-4 h-4 mr-2" />
                Clear
              </Button>
              <Button 
                type="submit" 
                disabled={createWorkEntryMutation.isPending}
                data-testid="button-save"
              >
                <Save className="w-4 h-4 mr-2" />
                {createWorkEntryMutation.isPending ? "Saving..." : "Add Entry"}
              </Button>
            </div>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

client/src/components/timesheet-table.ts.content:
import { Eye, Edit, Trash2, Check, X } from "lucide-react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Checkbox } from "@/components/ui/checkbox";

import type { WorkEntry, WorkEntryWithUser } from "@shared/schema";

interface TimesheetTableProps {
  timesheets: WorkEntry[] | WorkEntryWithUser[];
  title: string;
  showEmployeeInfo?: boolean;
  showActions?: boolean;
  showApprovalActions?: boolean; // New prop to control approve/reject visibility
  selectedIds?: string[];
  onSelectionChange?: (ids: string[]) => void;
}

export default function TimesheetTable({ 
  timesheets, 
  title, 
  showEmployeeInfo = false, 
  showActions = true,
  showApprovalActions = false,
  selectedIds = [],
  onSelectionChange
}: TimesheetTableProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const updateStatusMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: string }) => {
      const response = await apiRequest("PATCH", `/api/work-entries/${id}/status`, { status });
      return response.json();
    },
    onSuccess: (_, { status }) => {
      // Invalidate all work entries queries
      queryClient.invalidateQueries({ queryKey: ["/api/work-entries"] });

      // Invalidate all stats queries
      queryClient.invalidateQueries({ queryKey: ["/api/stats"] });

      // Invalidate users query for updated stats
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });

      toast({
        title: "Success",
        description: `Work entry ${status === "approved" ? "approved" : "rejected"} successfully!`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update timesheet status",
        variant: "destructive",
      });
    },
  });

  const deleteWorkEntryMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await apiRequest("DELETE", `/api/work-entries/${id}`);
      return response.json();
    },
    onSuccess: () => {
      // Invalidate all work entries queries
      queryClient.invalidateQueries({ queryKey: ["/api/work-entries"] });

      // Invalidate all stats queries
      queryClient.invalidateQueries({ queryKey: ["/api/stats"] });

      // Invalidate users query for updated stats
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });

      toast({
        title: "Success",
        description: "Work entry deleted successfully!",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete work entry",
        variant: "destructive",
      });
    },
  });

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "approved":
        return (
          <Badge variant="secondary" className="bg-green-100 text-green-800" data-testid="status-approved">
            <Check className="w-3 h-3 mr-1" />
            Approved
          </Badge>
        );
      case "rejected":
        return (
          <Badge variant="destructive" data-testid="status-rejected">
            <X className="w-3 h-3 mr-1" />
            Rejected
          </Badge>
        );
      default:
        return (
          <Badge variant="outline" className="bg-yellow-100 text-yellow-800" data-testid="status-pending">
            Pending
          </Badge>
        );
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (onSelectionChange) {
      onSelectionChange(checked ? timesheets.map(t => t.id) : []);
    }
  };

  const handleSelectOne = (id: string, checked: boolean) => {
    if (onSelectionChange) {
      const newSelection = checked 
        ? [...selectedIds, id]
        : selectedIds.filter(selectedId => selectedId !== id);
      onSelectionChange(newSelection);
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const getUserInitials = (name: string) => {
    return name.split(' ').map(n => n[0]).join('').toUpperCase();
  };

  return (
    <Card data-testid="timesheet-table">
      <CardHeader>
        <CardTitle>{title}</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="overflow-x-auto">
          <Table>
            <TableHeader>
              <TableRow>
                {onSelectionChange && (
                  <TableHead className="w-12">
                    <Checkbox
                      checked={selectedIds.length === timesheets.length && timesheets.length > 0}
                      onCheckedChange={handleSelectAll}
                      data-testid="checkbox-select-all"
                    />
                  </TableHead>
                )}
                {showEmployeeInfo && <TableHead>Employee</TableHead>}
                <TableHead>Date</TableHead>
                <TableHead>Time Spent</TableHead>
                <TableHead>Work Type</TableHead>
                <TableHead>Status</TableHead>
                {showActions && <TableHead>Actions</TableHead>}
              </TableRow>
            </TableHeader>
            <TableBody>
              {timesheets.length === 0 ? (
                <TableRow>
                  <TableCell 
                    colSpan={showEmployeeInfo ? 6 : 5} 
                    className="text-center text-muted-foreground py-8"
                    data-testid="empty-state"
                  >
                    No timesheet entries found
                  </TableCell>
                </TableRow>
              ) : (
                timesheets.map((timesheet) => (
                  <TableRow key={timesheet.id} className="hover:bg-muted/50" data-testid={`timesheet-row-${timesheet.id}`}>
                    {onSelectionChange && (
                      <TableCell>
                        <Checkbox
                          checked={selectedIds.includes(timesheet.id)}
                          onCheckedChange={(checked) => handleSelectOne(timesheet.id, checked as boolean)}
                          data-testid={`checkbox-${timesheet.id}`}
                        />
                      </TableCell>
                    )}
                    {showEmployeeInfo && 'user' in timesheet && (
                      <TableCell>
                        <div className="flex items-center">
                          <div className="w-8 h-8 bg-primary text-primary-foreground rounded-full flex items-center justify-center text-sm font-medium mr-3">
                            {getUserInitials(timesheet.user.firstName + ' ' + timesheet.user.lastName)}
                          </div>
                          <div>
                            <div className="text-sm font-medium text-foreground" data-testid={`employee-name-${timesheet.id}`}>
                              {timesheet.user.firstName} {timesheet.user.lastName}
                            </div>
                            <div className="text-sm text-muted-foreground" data-testid={`employee-dept-${timesheet.id}`}>
                              {timesheet.user.department}
                            </div>
                          </div>
                        </div>
                      </TableCell>
                    )}
                    <TableCell className="text-sm text-foreground" data-testid={`date-${timesheet.id}`}>
                      {formatDate(timesheet.date)}
                    </TableCell>
                    <TableCell className="text-sm text-foreground" data-testid={`time-spent-${timesheet.id}`}>
                      {timesheet.timeSpent} hrs
                    </TableCell>
                    <TableCell className="text-sm text-foreground" data-testid={`work-type-${timesheet.id}`}>
                      {timesheet.workType}
                    </TableCell>
                    <TableCell data-testid={`status-${timesheet.id}`}>
                      {getStatusBadge(timesheet.status)}
                    </TableCell>
                    {showActions && (
                      <TableCell className="text-sm space-x-2">
                        <Button
                          variant="ghost"
                          size="sm"
                          className="text-blue-600 hover:text-blue-800"
                          title="View Details"
                          data-testid={`button-view-${timesheet.id}`}
                        >
                          <Eye className="w-4 h-4" />
                        </Button>

                        {timesheet.status === "pending" && (
                          <>
                            {showApprovalActions || showEmployeeInfo ? (
                              <>
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  className="text-green-600 hover:text-green-800"
                                  title="Approve"
                                  onClick={() => {
                                    if (window.confirm("Are you sure you want to approve this timesheet entry?")) {
                                      updateStatusMutation.mutate({ id: timesheet.id, status: "approved" });
                                    }
                                  }}
                                  disabled={updateStatusMutation.isPending}
                                  data-testid={`button-approve-${timesheet.id}`}
                                >
                                  <Check className="w-4 h-4" />
                                </Button>
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  className="text-red-600 hover:text-red-800"
                                  title="Reject"
                                  onClick={() => {
                                    if (window.confirm("Are you sure you want to reject this timesheet entry?")) {
                                      updateStatusMutation.mutate({ id: timesheet.id, status: "rejected" });
                                    }
                                  }}
                                  disabled={updateStatusMutation.isPending}
                                  data-testid={`button-reject-${timesheet.id}`}
                                >
                                  <X className="w-4 h-4" />
                                </Button>
                              </>
                            ) : (
                              <>
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  className="text-primary hover:text-primary/80"
                                  title="Edit"
                                  data-testid={`button-edit-${timesheet.id}`}
                                >
                                  <Edit className="w-4 h-4" />
                                </Button>
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  className="text-destructive hover:text-destructive/80"
                                  title="Delete"
                                  onClick={() => {
                                    if (window.confirm("Are you sure you want to delete this work entry?")) {
                                      deleteWorkEntryMutation.mutate(timesheet.id);
                                    }
                                  }}
                                  data-testid={`button-delete-${timesheet.id}`}
                                >
                                  <Trash2 className="w-4 h-4" />
                                </Button>
                              </>
                            )}
                          </>
                        )}
                      </TableCell>
                    )}
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </div>
      </CardContent>
    </Card>
  );
}

