    queryKey: ["/api/stats"],
  });

  const { data: users = [] } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  const { data: allWorkEntries = [], isLoading: entriesLoading } = useQuery<WorkEntryWithUser[]>({
    queryKey: ["/api/work-entries"],
  });

  if (statsLoading || entriesLoading) {
