              <StatsCard
                title="Submitted Today"
                value={stats?.submittedToday || 0}
                icon={Clock}
                className="text-green-600"
                testId="stat-submitted-today"
              />
              <StatsCard
                title="Not Submitted Today"
                value={stats?.notSubmittedToday || 0}
                icon={Clock}
                className="text-red-600"
