import { ConfidentialClientApplication, AuthenticationResult } from "@azure/msal-node";
import axios from "axios";

interface MicrosoftUser {
  id: string;
  displayName: string;
  givenName: string;
  surname: string;
  mail: string;
  userPrincipalName: string;
}

class MicrosoftAuthService {
  private msalInstance: ConfidentialClientApplication;
  private redirectUri: string;

  constructor() {
    const clientId = process.env.MICROSOFT_CLIENT_ID;
    const clientSecret = process.env.MICROSOFT_CLIENT_SECRET;

    if (!clientId || !clientSecret) {
      throw new Error("Microsoft OAuth credentials not configured");
    }

    // Get the base URL from environment or construct from host
    const baseUrl = process.env.REPL_SLUG 
      ? `https://${process.env.REPL_SLUG}.${process.env.REPL_OWNER}.repl.co`
      : `http://localhost:5000`;

    this.redirectUri = `${baseUrl}/api/auth/microsoft/callback`;

    const msalConfig = {
      auth: {
        clientId,
        clientSecret,
        authority: "https://login.microsoftonline.com/common"
      }
    };

    this.msalInstance = new ConfidentialClientApplication(msalConfig);
  }

  async getAuthUrl(): Promise<string> {
    const authCodeUrlParameters = {
      scopes: ["user.read", "openid", "profile", "email"],
      redirectUri: this.redirectUri,
    };

    return await this.msalInstance.getAuthCodeUrl(authCodeUrlParameters);
  }

  async handleCallback(code: string): Promise<AuthenticationResult> {
    const tokenRequest = {
      code,
      scopes: ["user.read", "openid", "profile", "email"],
      redirectUri: this.redirectUri,
    };

    return await this.msalInstance.acquireTokenByCode(tokenRequest);
  }

  async getUserInfo(accessToken: string): Promise<MicrosoftUser> {
    const response = await axios.get('https://graph.microsoft.com/v1.0/me', {
      headers: {
        'Authorization': `Bearer ${accessToken}`
      }
    });

    return response.data;
  }
}

export const microsoftAuth = new MicrosoftAuthService();

